import Colors from "../../assets/colors";

export const boardStyles = {
  container: {
    width: "98vw",
    margin: "2rem auto",
  },
  column: {
    container: {
      width: "25rem",
      minHeight: "10rem",
    //   backgroundColor: Colors.offWhite,
      borderRadius: "6px",
      overflow: "hidden",
      paddingTop: "0px",
      boxShadow: `3px 3px 12px ${Colors.offWhite}`
    },
    span:{
        display: "flex",
        justifyContent: "space-between",
        alignItems: "center",
        marginTop: "-1.25rem",
        width: "24rem"
    },
    header:{
        width: "12rem",
        backgroundColor: Colors.base,
        color: Colors.white,
        padding: "0.6rem",
        fontWeight: "200",
        borderRadius: "12px"
    },
    newCard:{
        width: "4rem",
        height: "4rem",
        fontSize: "3rem",
        border: "none",
        borderRadius: "100%",
        backgroundColor: Colors.base,
        color: Colors.white
    }
  },
};
