import Colors from "../../assets/colors";

const smallButton = {
  width: "4.5rem",
  height: "2rem",
  fontSize: "1.3rem",
  border: "none",
  borderRadius: "8px",
  color: Colors.white,
  display: "flex",
  justifyContent: "center",
  alignItems: "center",
  marginBottom: "0.1rem"
};

export const boardStyles = {
  container: {
    width: "98vw",
    margin: "2rem auto",
  },
  column: {
    container: {
      width: "25rem",
      minHeight: "10rem",
      //   backgroundColor: Colors.offWhite,
      borderRadius: "6px",
      overflow: "hidden",
      paddingTop: "0px",
      boxShadow: `4px 6px 12px ${Colors.offWhite}`,
    },
    span: {
      display: "flex",
      justifyContent: "space-between",
      alignItems: "center",
      marginTop: "-1rem",
      width: "100%",
    },
    header: {
      width: "12rem",
      backgroundColor: Colors.base,
      color: Colors.white,
      padding: "0.6rem",
      fontWeight: "300",
      borderRadius: "12px",
      textAlign: "center",
      fontSize: "2.5rem"
    },
    newCard: {
        ...smallButton,
        backgroundColor: Colors.base,
    },
    edit:{
        ...smallButton,
        backgroundColor: Colors.light_accent
    }
  },
};
