import Colors from "../../assets/colors";

export const boardStyles = {
  container: {
    width: "98vw",
    margin: "2rem auto",
  },
  column: {
    container: {
      width: "30rem",
      minHeight: "10rem",
      backgroundColor: Colors.offWhite,
      borderRadius: "6px",
      overflow: "hidden",
      paddingTop: "0px",
    },
    header:{
        width: "100%",
        marginTop: "0",
        backgroundColor: Colors.base,
        color: Colors.white,
        padding: "0.5rem",
        fontWeight: "200"
    }
  },
};
