import Colors from "../../assets/colors";

export const boardStyles = {
  container: {
    width: "98vw",
    margin: "2rem auto",
  },
  column: {
    container: {
      width: "25rem",
      minHeight: "10rem",
    //   backgroundColor: Colors.offWhite,
      borderRadius: "6px",
      overflow: "hidden",
      paddingTop: "0px",
      boxShadow: `3px 3px 12px ${Colors.offWhite}`
    },
    header:{
        width: "14rem",
        marginTop: "1rem",
        marginLeft: "1rem",
        backgroundColor: Colors.base,
        color: Colors.white,
        padding: "0.5rem",
        fontWeight: "200",
        borderRadius: "18px"
    }
  },
};
