import { boardStyles } from "../boardStyles";
import Card from "./card";

function Column({ title, cards }) {
  return (
    <div style={boardStyles.column.container}>
      <h1 style={boardStyles.column.header}>{title}</h1>
      <div style={boardStyles.column.cardContainer}>
          {cards.length>0?
            cards.map(card => {<Card/>})
          :null}
      </div>
    </div>
  );
}

export default Column;
