import Colors from "../../assets/colors";

export const boardStyles = {
  container: {
    width: "98vw",
    margin: "2rem auto",
    display: "flex",
    flexFlow: "row no-wrap",
    overflowX: "auto"
  },
  column: {
    container: {
      minWidth: "20rem",
      width: "30rem",
      minHeight: "10rem",
      maxHeight: "80vh",
      borderRadius: "6px",
      borderRight: `1px solid ${Colors.offWhite}`,
      overflow: "auto",
      paddingTop: "0px",
      marginRight: "1.5rem"
    },
    header: {
      color: Colors.base,
      fontWeight: "400",
      fontSize: "2rem",
    },
    cardContainer:{
        width: "90%",
        margin: "0 auto",
        display: "flex",
        flexFlow: "column"
    }
  },
};


export const cardStyles = {
    container: {
        width: "100%",
        borderRadius: "8px",
        marginBottom: "0.5rem",
        transition: "all ease 0.2s"
    }
}
