import Colors from "../../assets/colors";

export const boardStyles = {
  container: {
    width: "98vw",
    margin: "2rem auto",
  },
  column: {
    container: {
      width: "25rem",
      minHeight: "10rem",
    //   backgroundColor: Colors.offWhite,
      borderRadius: "6px",
      overflow: "hidden",
      paddingTop: "0px",
      boxShadow: `3px 3px 12px ${Colors.offWhite}`
    },
    span:{
        display: "flex",
        justifyContent: "space-between",
        marginTop: "0",
    },
    header:{
        width: "12rem",
        backgroundColor: Colors.base,
        color: Colors.white,
        padding: "0.6rem",
        fontWeight: "200",
        borderRadius: "12px"
    },
    newCard:{
        height: "2rem"
    }
  },
};
