import Card from "./innerBoardComponents/card";
import Column from "./innerBoardComponents/column";

function Board({ boardId, boardData, getCompleteBoard }) {
  useEffect(() => {
    getCompleteBoard(boardId);
  }, []);

  const [doneLoading, setDoneLoading] = useState(false);
  useEffect(() => {
    if (boardData.length > 0) {
      setDoneLoading(true);
    }
  }, [boardData]);

  return (
    <div style={boardStyles.container}>
      {doneLoading
        ? boardData.map((col) => <Column title={col.title}>
            {col.cards.map((card) => {<Card />})}
