import Colors from "../../assets/colors";

const smallButton = {
  width: "1.5rem",
  height: "1.5rem",
  fontSize: "1rem",
  border: "none",
  borderRadius: "100%",
  color: Colors.white,
  display: "flex",
  justifyContent: "center",
  alignItems: "center",
  margin: "0.1rem 0.3rem"
};

export const boardStyles = {
  container: {
    width: "98vw",
    margin: "2rem auto",
    display: "flex",
    flexFlow: "row no-wrap"
  },
  column: {
    container: {
      width: "25rem",
      minHeight: "10rem",
      borderRadius: "6px",
      borderRight: `1px solid ${Colors.offWhite}`,
      overflow: "hidden",
      paddingTop: "0px",
      marginRight: "1.5rem"
    },
    header: {
      color: Colors.base,
      fontWeight: "400",
      fontSize: "2rem",
      marginLeft: "6.45%",
      marginBottom: "0",
      textAlign: "left"
    },
    newCard: {
        ...smallButton,
        backgroundColor: Colors.base,
    },
    dragPoint:{
        backgroundColor: Colors.base,
        color: Colors.white,
        flexBasis: "75%",
        height: "4px",
        borderRadius: "8px"
    },
    edit:{
        ...smallButton,
        backgroundColor: Colors.light_accent
    },
    cardContainer:{
        width: "98%",
        margin: "0 auto",
        display: "flex",
        flexFlow: "column"
    }
  },
};


export const cardStyles = {
    container: {
        width: "100%",
        borderRadius: "8px",
        marginBottom: "0.5rem"
    }
}
