import Colors from "../../assets/colors";

const smallButton = {
  width: "2.5rem",
  height: "1.5rem",
  fontSize: "1rem",
  border: "none",
  borderRadius: "16%",
  color: Colors.white,
  display: "flex",
  justifyContent: "center",
  alignItems: "center",
  margin: "0.1rem 0.3rem"
};

export const boardStyles = {
  container: {
    width: "98vw",
    margin: "2rem auto",
    display: "flex",
    flexFlow: "row no-wrap"
  },
  column: {
    container: {
      width: "25rem",
      minHeight: "10rem",
      borderRadius: "6px",
      borderRight: `1px solid ${Colors.offWhite}`,
      overflow: "hidden",
      paddingTop: "0px",
      marginRight: "1.5rem"
    },
    span: {
      display: "flex",
      justifyContent: "space-between",
      alignItems: "flex-start",
    //   marginTop: "-1rem",
      width: "100%",
    },
    header: {
      width: "16rem",
      color: Colors.base,
      padding: "0.6rem",
      fontWeight: "300",
      borderRadius: "0 8px 0 8px",
      textAlign: "center",
      fontSize: "2rem"
    },
    newCard: {
        ...smallButton,
        backgroundColor: Colors.base,
    },
    edit:{
        ...smallButton,
        backgroundColor: Colors.light_accent
    },
    cardContainer:{
        width: "98%",
        margin: "0 auto",
        display: "flex",
        flexFlow: "column"
    }
  },
};


export const cardStyles = {
    container: {
        width: "100%",
        borderRadius: "8px",
        marginBottom: "0.5rem"
    }
}
